import React, { Component } from 'react';
import ChildComponent from './ChildComponent';

class ParentComponent extends Component{
    
    constructor(){
        super()
        this.state={
            className:"subject",
            parentName:'Parent'
        }
        this.greetParent=this.greetParent.bind(this)
    }
    
    greetParent(child){
        alert(`hello ${this.state.parentName} frpm ${child}`)
    }

    render(){
        return(
            <div>
              <ChildComponent className="subject" greetHandler={this.greetParent}/>
            </div>
        )
    }
}

export default ParentComponent;
