import React, { Component } from 'react'
import axios from 'axios';

class PostList extends Component {
     constructor(props) {
         super(props)
     
         this.state = {
              posts:[],
              error:"",
              team_id:""
         }
     }
     handleTeam_Id=(e =>{
         this.setState({
             team_id:e.target.value
         })
     })
     componentDidMount(){
         axios.get('https://cricapi.com/api/matches/rXH9Y3RJu3YVsF51R6FSTwLR24h2')
         .then(Response=>{
             console.log(Response.data.matches)
             this.setState({
                posts:Response.data.matches
             })
         })
         .catch(error=>{
            //console.log(error)
             this.setState({
                 error: "bahot error"
             })
         })
     }
        // axios.get('http://cricapi.com/api/cricketScore/rXH9Y3RJu3YVsF51R6FSTwLR24h2/121653?unique_id=946765')
    render() {
        const {posts,error,team_id}=this.state
        //console.log(posts)
        return (
            <div>
                <h1>List Of Matches</h1>
                {
                 posts.length?
                 posts.map(post=>
                 <li><div key={post["unique_id"]}>{post["team-1"]} VS {post["team-2"]}
                 <h4>Winner of the match{post["winner_team"]}</h4></div>
                 <button value={team_id} onClick={this.handleTeam_Id}>Stats</button></li>
                 )

                 :null
                }
            </div>
        )
    }
}

export default PostList
