import React,{Component} from 'react';

class Form extends Component{
    constructor(props){
        super(props)

        this.state={
            username:'shivam',
            comment:"hey shivam how are you!",
            frontend:"React"
        }
    }
    handleusernameChange=(event)=>{
         this.setState({
             username:event.target.value
         })
    }
    handlecommentChange=(event)=>{
        this.setState({
            comment:event.target.value
        })
    }
    handletopicchange=(event)=>{
        this.setState( {
            frontend:event.target.value
        })
    }
    handlesubmit=(event)=>{
        alert(`${this.state.username} ${this.state.comment} ${this.state.frontend}`)
        event.preventDefault()
    }
   render(){
       return(
            <form  onSubmit={this.handlesubmit}>
                <React.Fragment>
       <h1>{this.state.username}</h1>
                    <label>Username: </label>
                    <input type='text'value={this.state.username}
                    onChange={this.handleusernameChange}/>
                </React.Fragment>
                    <p>{this.state.comment}</p>
                    <label>Comment: </label>
                    <input type="text" value={this.state.comment}
                    onChange={this.handlecommentChange}/>
                
       <p>{this.state.frontend}</p>
                    <label htmlFor="frontend">Choose a Frame:  </label>
                    <select value={this.state.frontend} 
                    onChange={this.handletopicchange}>
                        <option value="react">React</option>
                        <option value="angular">Angular</option>
                        <option value="vue">Vue</option>
                    </select>
                
                <button type="submit">submit</button>
            </form>
       )
   }
}

export default Form;
