import Axios from 'axios'
import React, { useEffect, useState } from 'react'

function CricketDataFetch() {
    const[posts,setposts]=useState([])
    const[id,setId]=useState([])

    useEffect(()=>{
        Axios.get(`https://cricapi.com/api/matches/rXH9Y3RJu3YVsF51R6FSTwLR24h2` )
        .then(response =>{
           // console.log(response.data.matches)
            setposts(response.data.matches)
        })
        .catch(error=>{
            console.log(error)
        })
    },[])
    handleId= async(id)=>{
        setId({id:id})
    }
    return (
        <div><h1>list of matches:</h1>
        {
            <ul>
            {posts.map(post=>(
            <li key={post["unique_id"]} >{post["team-1"]} <b>VS</b> {post["team-2"]}
            <b>WINNER</b> {post["winner_team"]} 
            <button value={id} onChange={handleId(post["unique_id"])} >
                Stats</button>
            </li>
            ))
            
            }
            </ul>
        }
        </div>
    )
}

export default CricketDataFetch
