import React,{Component} from 'react';
import PureComp from './PureComponent';
import RegComp from './RegularComponent';

class ParComp extends Component{
    constructor(props){
        super(props)
        this.state={
            name:"shivam"
        }
    }
    componentDidMount(){
        setInterval(()=>{
            this.setState({
               name:"shivam"
            })
        },2000)
    }
    render(){
        console.log("Parent")
        return(
            <div>
              Welcome to ParComp 
              <RegComp name={this.state.name}/>
              <PureComp name={this.state.name}/>
            </div>
        )
    }
}

export default ParComp;
